import React from "react";

import Navbar from "./components/Navbar";
import Service from "./components/Service";
// import Footer from "./components/Footer";

import CssBaseline from "@mui/material/CssBaseline";
import Container from "@mui/material/Container";
import FormControlLabel from "@mui/material/FormControlLabel";
import Switch from "@mui/material/Switch";
import Typography from "@mui/material/Typography";
import Paper from "@mui/material/Paper";
import Stack from "@mui/material/Stack";
import Button from "@mui/material/Button";
import Grid from "@mui/material/Unstable_Grid2";

import ArrowForwardIcon from '@mui/icons-material/ArrowForward';
import ConstructionIcon from '@mui/icons-material/Construction';
import HardwareIcon from '@mui/icons-material/Hardware';
import WaterIcon from '@mui/icons-material/Water';

import ThemeProvider from "@mui/material/styles/ThemeProvider";
import createTheme from "@mui/material/styles/createTheme";
import useScrollTrigger from '@mui/material/useScrollTrigger';


// https://m2.material.io/resources/color/#!/?view.left=0&view.right=1&primary.color=1b4972
const lightTheme = createTheme({
  palette: {
    primary: {
      light: "#4b94b7",
      main: "#086788",
      dark: "#003c5a",
      contrastText: "#fff",
    },
    secondary: {
      light: "#a68f89",
      main: "#77625c",
      dark: "#4b3833",
    },
    background: {
      default: "#fff",
    },
  },
});

// #086788, #07A0C3, #C5C9A4, #8E6E53, #77625C

const darkTheme = createTheme({
  palette: {
    mode: "dark",
    secondary: {
      main: "#77625C",
    },
  },
});

export default function App(): JSX.Element {
  const [darkMode, setDarkMode] = React.useState(false);

  const handleChange = (event: React.ChangeEvent<HTMLInputElement>): void => {
    setDarkMode(event.target.checked);
  };

  const thresholdTrigger: boolean = useScrollTrigger({
    disableHysteresis: true,
    threshold: 80,
  });

  return (
    <ThemeProvider theme={darkMode ? darkTheme : lightTheme}>
      <CssBaseline />

      <Navbar transparent={!thresholdTrigger} />

      <Paper elevation={2} sx={{
        width: "100%",
        backgroundColor: "#404040",
        backgroundImage:
          'linear-gradient(160deg, #003c5a, rgba(0, 0, 0, 0)), url("./images/drill-with-dog.jpeg")',
        backgroundSize: "cover",
        backgroundPosition: "center center",
      }}>
        <Container sx={{ paddingY: 10, paddingTop: 15 }}>
          <Grid container>
            <Grid xs={12} md={8}>
              <Typography variant="h2" color="white" mb={3} sx={{fontFamily: "Krona One"}}>
                <span style={{ color: '#4b94b7' }}>Integrity</span> Well and Drilling Services
              </Typography>

              <Typography variant="h4" color="white" mb={6}>
                We provide reliable, efficient, and high-quality well drilling
                services to the Snowflake, Arizona community.
              </Typography>

              <Stack direction="row" spacing={3}>
                <Button variant="contained" size="large" color="secondary">Get A Free Estimate</Button>
                <Button sx={{ color: 'white' }} endIcon={<ArrowForwardIcon />}>View Our Services</Button>
              </Stack>
            </Grid>
          </Grid>
        </Container>
      </Paper>

      <Container maxWidth="md">
        <Stack direction="row" justifyContent="space-between">
          <Typography variant="h3" mt={10} mb={3} sx={{fontFamily: "Krona One"}}>
            Our <span style={{ color: '#4b94b7' }}>Services</span>
          </Typography>
          <Button size="small">Learn More</Button>
        </Stack>

        <Grid container spacing={3} alignItems="stretch" mb={5}>
          <Grid xs={12} sm={4}>
            <Service title="Well construction" icon={<ConstructionIcon sx={{ fontSize: 84 }} />}>
              Our well construction services involve the drilling of new wells, installation of casing or liners, and placement of screens or filters to prevent contamination.
            </Service>
          </Grid>

          <Grid xs={12} sm={4}>
            <Service title="Pump services" icon={<HardwareIcon sx={{ fontSize: 84 }} />}>
              We use top-of-the-line equipment and technology to provide reliable installation, repair, and maintenance of well pumps.
            </Service>
          </Grid>

          <Grid xs={12} sm={4}>
            <Service title="Water drilling" icon={<WaterIcon sx={{ fontSize: 84 }} />}>
              Our water drilling services offer reliable access to clean water. Count on us for safe, efficient, and affordable water drilling services.
            </Service>
          </Grid>
        </Grid>

        <Typography variant="h3" mt={10} mb={3} sx={{fontFamily: "Krona One"}}>About Us</Typography>

        <Typography paragraph>
          Integrity Well Drilling Services is a brand-new well drilling company
          that is committed to providing high-quality services to residential,
          commercial, and agricultural clients. Our team of experienced
          professionals is dedicated to delivering exceptional results and
          building lasting relationships with our clients.
        </Typography>

        <Typography paragraph>
          At Integrity Well Drilling Services, we understand the importance of
          having access to clean and reliable water, and we are equipped with
          the latest equipment and technology to ensure that the wells we drill
          are of the highest quality. We take pride in our ability to provide
          personalized services that are tailored to meet the specific needs of
          each client, and we work closely with them to ensure that their
          projects are completed on time and within budget.
        </Typography>

        <Typography paragraph>
          Although we are a new company, we bring a wealth of experience to the
          table, and we are committed to providing top-notch services that are
          both reliable and affordable. We offer a wide range of services,
          including well drilling, well pump installation, well inspections, and
          more. We are equipped to handle projects of all sizes and
          complexities, and we approach each job with the same level of
          dedication and professionalism.
        </Typography>

        <Typography paragraph>
          At Integrity Well Drilling Services, customer satisfaction is our top
          priority. We believe in delivering exceptional services that exceed
          our clients' expectations, and we go above and beyond to ensure that
          each client is satisfied with the results. We are committed to
          building lasting relationships with our clients and becoming their
          go-to provider for all their well drilling needs.
        </Typography>

        <Typography paragraph>
          Although we are a new company, we are fully licensed and insured,
          providing our clients with peace of mind knowing that their projects
          are in good hands. We are committed to safety and adhere to all
          industry standards and regulations.
        </Typography>

        <Typography paragraph>
          If you're in need of well drilling services, look no further than
          Integrity Well Drilling Services. We are a new company that is
          dedicated to delivering exceptional results, and we are committed to
          providing top-notch services that are both reliable and affordable.
          Contact us today to learn more about our services and how we can help
          you with your next project.
        </Typography>

        <FormControlLabel
          control={<Switch checked={darkMode} onChange={handleChange} />}
          label="Dark Mode"
        />
      </Container>

      {/* <Footer /> */}
    </ThemeProvider>
  );
}
