import React from 'react';
import Container from '@mui/material/Container';
import Stack from '@mui/material/Stack';
import Button from '@mui/material/Button';
import AppBar from '@mui/material/AppBar';
import Typography from '@mui/material/Typography';
import Popover from '@mui/material/Popover';

import PhoneIcon from '@mui/icons-material/Phone';
import EmailIcon from '@mui/icons-material/Email';
import MapIcon from '@mui/icons-material/Map';


function ContactUs(): JSX.Element {
    const width: number = 240;

    return (
        <Container sx={{ marginY: 3 }}>
            <Stack minWidth="100%" width={240} direction="row" justifyContent="space-between" my={2}>
                <PhoneIcon />
                <Typography variant="subtitle1">480.495.2463</Typography>
            </Stack>

            <Stack width={width} direction="row" justifyContent="space-between" my={2}>
                <EmailIcon />
                <Typography variant="subtitle1">integritywds@gmail.com</Typography>
            </Stack>

            {/* <Stack width={width} direction="row" justifyContent="space-between" my={2}>
                <MapIcon />
                <Typography variant="subtitle1" textAlign="right">1341 E. Old West Highway Apache Junction, Arizona 85119</Typography>
            </Stack> */}
        </Container>
    );
}

type NavButtonProps = {
    color: string;
    children: string;
    active?: boolean;
};

  class TabButton extends React.Component<NavButtonProps> {
    public render(): JSX.Element {
      return (
        <Button key={this.props.children} sx={{
          fontWeight: 500,
          color: this.props.color,
          position: 'relative',

          '&:before': {
            content: "''",
            position: 'absolute',
            width: this.props.active ? '100%' : 0,
            height: '2px',
            bottom: 0,
            backgroundColor: this.props.color,
            visibility: this.props.active ? 'visible' : 'hidden',
            transition: 'all 0.1s ease-in-out',
          },

          '&:hover:before': {
            visibility: 'visible',
            width: '100%',
          },
        }}>
          {this.props.children}
        </Button>
      );
    }
  }

type PopoverTabProps = {
    backgroundColor: string;
    color: string;
    children: string;
};

type PopoverTabState = {
    anchorEl?: HTMLButtonElement;
};

class PopoverTab extends React.Component<PopoverTabProps> {
    public state: PopoverTabState = {
        anchorEl: undefined,
    };

    constructor(props: PopoverTabProps) {
        super(props);

        this.handleClick = this.handleClick.bind(this);
        this.handleClose = this.handleClose.bind(this);
    }

    private handleClick(event: React.MouseEvent<HTMLButtonElement>): void {
        this.setState({ anchorEl: event.currentTarget });
      };

    private handleClose(): void {
        this.setState({ anchorEl: undefined });
    }

    public render(): JSX.Element {
        const open: boolean = Boolean(this.state.anchorEl);
        const id: string | undefined = open ? 'simple-popover' : undefined;

        return (
            <>
                <Button aria-describedby={id} variant="contained" onClick={this.handleClick} sx={{
                    backgroundColor: this.props.backgroundColor,
                    color: this.props.color,
                }}>
                    {this.props.children}
                </Button>

                <Popover id={id} open={open} anchorEl={this.state.anchorEl} onClose={this.handleClose} 
                    transformOrigin={{
                        vertical: 'top',
                        horizontal: 'right',
                    }}
                    anchorOrigin={{ 
                        vertical: 'bottom',
                        horizontal: 'right',
                    }}
                >
                    <ContactUs />
                </Popover>
            </>
        );
    }
}

type NavbarProps = {
    transparent?: boolean;
};

export default class Navbar extends React.Component<NavbarProps> {
    public render(): JSX.Element {
        const elevation: number = this.props.transparent ? 0 : 3;
        const backgroundColor: 'primary' | 'transparent' = this.props.transparent ? 'transparent' : 'primary';
        const textColor: string = 'white';
        const containedBackgroundColor: string = this.props.transparent ? 'primary' : 'white';
        const containedTextColor: string = this.props.transparent ? 'white' : '#086788';

        return (
            <AppBar position="fixed" color={backgroundColor} elevation={elevation} sx={{ paddingY: 2, transition: '0.1s' }}>
                <Container>
                    <Stack direction="row" justifyContent="space-between">
                        <Typography variant="h6" color="white">
                            IWDS
                        </Typography>

                        <Stack direction="row" spacing={3} sx={{ display: { xs: 'none', md: 'block' }}}>
                            <TabButton color={textColor}>Free Estimate</TabButton>
                            <TabButton color={textColor}>Services</TabButton>
                            <TabButton color={textColor}>About</TabButton>
                            <PopoverTab backgroundColor={containedBackgroundColor} color={containedTextColor}>
                                Contact Us
                            </PopoverTab>
                        </Stack>
                    </Stack>
                </Container>
            </AppBar>
        );
    }
}
